import * as firebase from 'firebase';
import Rebase from "re-base";
import { devConfig } from './config';

const app = firebase.initializeApp(devConfig);
const base = Rebase.createClass(firebase.database(app));
const auth = app.auth();

export {
  auth,
  base
}
