import React, {Component} from 'react';
import {base} from './../firebase/firebase';
import { Card } from 'antd';

  class Dashboard extends Component {
    constructor(props) {
      super(props);

      this.state={
          permission:null
      }
    }

    componentDidMount() {
        this.ref = base.syncState(`users/${this.props.user.uid}/${this.props.role}/permission`, {
          context: this,
          state: "permission"
        });
      }

    render() {
      return  (
          <div className="main">
            <Card>
              <p>Hello {this.props.user.uid}</p>
              <p>{this.props.role}</p>
              <p>{this.state.permission}</p>
            </Card>
          </div>
        )

    }
  }


  export default Dashboard;
