import { auth } from './firebase';

/**
 * Create user session
 * @param {string} action - createUser, signIn
 * @param {string} email 
 * @param {string} password 
 * @param {string} role
 */
const userSession = (action, email, password, role) => auth[`${action}WithEmailAndPassword`](email, password, role);

/**
 * Destroy current user session
 */
const logout = () => auth.signOut();

export {
  userSession,
  logout
}
