import React from 'react';
import { Consumer } from '../components/AppProvider';

const FlashMessage = () => <Consumer>
  {({ state, ...context }) => state.message && <div role="alert" aria-live="assertive" aria-atomic="true" className="toast" data-autohide="false">
      <div className="toast-header">
        <strong class="mr-auto">Bootstrap</strong>
        <small>11 mins ago</small>
        <button 
          type="button" 
          className="close"
          aria-label="Close"
          onClick={() => context.clearMessage()}
        >
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="toast-body">
        {state.message}
      </div>
    </div>
  }
</Consumer>;

export default FlashMessage;
