import React, {Component, createRef} from 'react';
import {base} from './../firebase/firebase';
import { Card, Icon } from 'antd';

  class Dashboard extends Component {
    constructor(props) {
      super(props);

      this.state={
          permission:null,
          stations: null,

          createStationFormVisible: false
      }

      this.stationNameRef = createRef();
      this.stationAddressRef = createRef();
    }

    componentDidMount() {
      this.ref = base.syncState(`users/${this.props.user.uid}/${this.props.role}/permission`, {
        context: this,
        state: "permission"
      });

      this.ref = base.syncState(`stations`, {
        context: this,
        state: "stations"
      });
    }

    createStation = event => {
      event.preventDefault();
      const station = {
        [this.stationNameRef.current.value]:this.stationAddressRef.current.value
      };
      this.addStation(this.stationNameRef.current.value, this.stationAddressRef.current.value);
      event.currentTarget.reset();
    };

    addStation = (name, address) => {
      const stations = { ...this.state.stations };
      stations[`${name}`] =  address;
      this.setState({ stations });
    };

    deleteStation = name => {
      const stations = {...this.state.stations}
      console.log(stations[`${name}`])
      stations[`${name}`] = null;
      this.setState({stations})
    }

    createStationFormShow = () => this.setState({createStationFormVisible:!this.state.createStationFormVisible})

    render() {
      return  (
          <div className="main">
            {this.state.stations?
              <>        
                <Card 
                  style={{width: '100%', margin: "0 25px"}} 
                  title="Stations"
                  extra={<Icon type="edit" key="edit" onClick={this.createStationFormShow}/>}
                >
                  <table style={{color: "rgba(0, 0, 0, 0.5)"}} className="table table-borderless">
                    {Object.keys(this.state.stations).length>0?
                    <thead>
                      <tr style={{border: "1px solid rgba(0, 0, 0, 0.1)"}}>
                        <th scope="col">Station Name</th>
                        <th scope="col">Address</th>
                        <th scope="col">Action</th>
                      </tr>
                    </thead>:''}
                    <tbody>
                    {Object.keys(this.state.stations).map(key => (
                      <tr key={key} style={{border: "1px solid rgba(0, 0, 0, 0.1)"}}>
                        <td>{key}</td>
                        <td onDoubleClick={()=>console.log("Ok")}>{this.state.stations[key]}</td>
                        <td><Icon type="delete" key="delete" onClick={()=>this.deleteStation(key)}/></td>
                      </tr>           
                    ))}
                    </tbody>
                  </table>
                </Card>

                {this.state.createStationFormVisible === true?
                  <Card  
                    style={{width: '100%', margin: "25px"}} 
                    title="Add New Station"
                  >
                    <form onSubmit={this.createStation}>
                      <div className="form-group">
                      <input
                        name="stationName"
                        className="form-control"
                        type="text"
                        ref={this.stationNameRef}
                      />
                      </div>
                      <div className="form-group">
                      <input
                        name="stationAddress"
                        className="form-control"
                        type="text"
                        autoComplete="none"
                        ref={this.stationAddressRef}
                      />
                      </div>
                      <button 
                        type="submit"
                        className="btn btn-primary"
                      >Submit</button>
                    </form>
                  </Card>:''
                }
              </>
              :''
            }
          </div>
        )

    }
  }


  export default Dashboard;
