import React, {Component, createRef} from 'react';

import Stations from './Stations';
import Manufactures from './Manufactures';

  class Dashboard extends Component {
    constructor(props) {
      super(props);

      this.state={
          permission:null,
          stations: null,

          createStationFormVisible: false
      }

      this.stationNameRef = createRef();
      this.stationAddressRef = createRef();
    }

    componentDidMount() {
      this.ref = base.syncState(`users/${this.props.user.uid}/${this.props.role}/permission`, {
        context: this,
        state: "permission"
      });

      this.ref = base.syncState(`manufactures`, {
        context: this,
        state: "manufactures"
      });
    }


    render() {
      return  (
        <>
          <div className="top-nav">
            <ul className="nav nav-tabs">
              <li className="nav-item">
                <a className="nav-link active" href="#stations">Stations</a>
              </li>

              <li className="nav-item">
                <a className="nav-link" href="#manufactures">Manufactures</a>
              </li>
            </ul>
          </div>
          <div className="main">
            <Stations />
            <Manufactures />
          </div>
        </>
      )
    }
  }


  export default Dashboard;
