import {base} from './../firebase/firebase';
import { Card } from 'antd';

  class Dashboard extends Component {
    constructor(props) {
      super(props);

      this.state={
          permission:null
      }
    }

    componentDidMount() {
        this.ref = base.syncState(`users/${this.props.user.uid}/${this.props.role}/permission`, {
          context: this,
          state: "permission"
        });

        console.log(base)
        console.log(this.ref)
      }

    render() {
      console.log(this.props.user)
      console.log(this.state.user)
      return  (
          <Card>
            <h1>Hello {this.props.user.uid}</h1>
            <h3>{this.props.role}</h3>
            <p>{this.state.permission}</p>
          </Card>
        )

    }
  }


  export default Dashboard;
