import React, {Component, createRef} from 'react';
import {base} from './../firebase/firebase';
import { Card } from 'antd';

  class Dashboard extends Component {
    constructor(props) {
      super(props);

      this.state={
          permission:null,
          stations: null
      }

      this.stationNameRef = createRef();
      this.stationAddressRef = createRef();
    }

    componentDidMount() {
        this.ref = base.syncState(`users/${this.props.user.uid}/${this.props.role}/permission`, {
          context: this,
          state: "permission"
        });

        this.ref = base.syncState(`stations`, {
          context: this,
          state: "stations"
        });
      }

    createStation = event => {
        event.preventDefault();
        const station = {
          [this.stationNameRef.current.value]:this.stationAddressRef.current.value
        };
        this.props.addStation(station);
        event.currentTarget.reset();
      };

    render() {
      return  (
          <div className="main">
            {this.state.stations?
              <>
                <div style={{width: "50%", padding:'0 30px'}}>
                  <Card  title="Stations" style={{width: "100%"}}>
                    <table style={{color: "rgba(0, 0, 0, 0.5)"}} className="table table-borderless">
                      <thead>
                        <tr style={{border: "1px solid rgba(0, 0, 0, 0.1)"}}>
                          <th scope="col">Station Name</th>
                          <th scope="col">Address</th>
                        </tr>
                      </thead>
                      <tbody>
                      {Object.keys(this.state.stations).map(key => (
                        <tr style={{border: "1px solid rgba(0, 0, 0, 0.1)"}}>
                          <td>{key}</td>
                          <td>{this.state.stations[key].address}</td>
                        </tr>           
                      ))}
                      </tbody>
                    </table>
                  </Card>
                </div>
                <div style={{width: "50%", padding: '0 30px'}}>
                  <Card  title="Add New Station" style={{width: "100%"}}>
                    <form onSubmit={this.createStation}>
                      <div className="form-group">
                      <input
                        name="stationName"
                        className="form-control"
                        type="text"
                        ref={this.stationNameRef}
                      />
                      </div>
                      <div className="form-group">
                      <input
                        name="stationAddress"
                        className="form-control"
                        type="text"
                        autoComplete="none"
                        ref={this.stationAddressRef}
                      />
                      </div>
                      <button 
                        type="submit"
                        className="btn btn-primary"
                      >Submit</button>
                    </form>
                  </Card>
                </div>
              </>
              :''
            }
          </div>
        )

    }
  }


  export default Dashboard;
