import React, {Component} from 'react';
import {base} from './../firebase/firebase';
import { Card } from 'antd';

  class Dashboard extends Component {
    constructor(props) {
      super(props);

      this.state={
          permission:null,
          stations: null
      }
    }

    componentDidMount() {
        this.ref = base.syncState(`users/${this.props.user.uid}/${this.props.role}/permission`, {
          context: this,
          state: "permission"
        });

        this.ref = base.syncState(`stations`, {
          context: this,
          state: "stations"
        });
      }

    render() {

      console.log(this.state.stations)
      return  (
          <div className="main">
            {this.state.stations?
              <Card  title="Stations" style={{width: "100%"}}>
                <table className="table table-borderless">
                  <thead>
                    <tr>
                      <th scope="col">Station Name</th>
                      <th scope="col">Address</th>
                    </tr>
                  </thead>
                  <tbody>
                  {Object.keys(this.state.stations).map(key => (
                    <tr>
                      <td>{key}</td>
                      <td>{this.state.stations[key].address}</td>
                    </tr>           
                  ))}
                  </tbody>
                </table>
              </Card>
              :''
            }
          </div>
        )

    }
  }


  export default Dashboard;
