import React, {Component} from 'react';
import {base} from '../firebase/firebase';
import Stations from './Stations';
import Manufactures from './Manufactures';
import Products from './Proructs';
import Intents from './Intents';


  class Dashboard extends Component {
    constructor(props) {
      super(props);

      this.state={
          permission:null,
          station: null
      }
    }

    componentDidMount() {
      this.ref = base.syncState(`users/${this.props.user.uid}/${this.props.role}/permission`, {
        context: this,
        state: "permission"
      });

      console.log(`users/${this.props.user.uid}/${this.props.role}/station`)
      this.ref = base.syncState(`users/${this.props.user.uid}/${this.props.role}/station`, {
        context: this,
        state: "station"
      });

      console.log(this.state.station)
    }


    render() {
      return  (
        <>
          <div style={{
              background:'#007bff',
              position: 'sticky',
              top: 0,
              zIndex: 100
            }} 
            className="top-nav"
          >
            <ul className="nav nav-tabs">
              <li className="nav-item">
                <a style={{color: 'black'}} className="nav-link" href="#stations">Stations</a>
              </li>

              <li className="nav-item">
                <a style={{color: 'black'}} className="nav-link" href="#manufactures">Manufactures</a>
              </li>

              <li className="nav-item">
                <a style={{color: 'black'}} className="nav-link" href="#products">Products</a>
              </li>
            </ul>
          </div>
          <div className="main">
            <Stations />
            <Manufactures />
            <Products />
            <Intents station={this.state.station} />
          </div>
        </>
      )
    }
  }


  export default Dashboard;
