import React, {Component} from 'react';
import {base} from '../firebase/firebase';
import Stations from './Stations';
import Manufactures from './Manufactures';

  class Dashboard extends Component {
    constructor(props) {
      super(props);

      this.state={
          permission:null
      }
    }

    componentDidMount() {
      this.ref = base.syncState(`users/${this.props.user.uid}/${this.props.role}/permission`, {
        context: this,
        state: "permission"
      });
    }


    render() {
      return  (
        <>
          <div style={{background:'#f05545'}} className="top-nav">
            <ul className="nav nav-tabs">
              <li className="nav-item">
                <a className="nav-link" href="#stations">Stations</a>
              </li>

              <li className="nav-item">
                <a className="nav-link" href="#manufactures">Manufactures</a>
              </li>
            </ul>
          </div>
          <div className="main">
            <Stations />
            <Manufactures />
          </div>
        </>
      )
    }
  }


  export default Dashboard;
